import styled from "styled-components"
import searchImg from "../images/search.png"

const SearchContent = styled.div`
    display: flex;
    align-items: center;
    background-color: white;
    height: 2.8rem;
    padding: 0 1rem;
    border-radius: 1rem;
`

const SearchForm = styled.form` 
    display: flex;
    
`

const SearchInput = styled.input`
    width: 30rem;
    background: transparent;
    border: none;
    box-sizing: border-box;

    &:focus-visible {
        outline: none;
    }
`

const SearchImg = styled.img`
    width: 1.5rem;
    height: 1.5rem;
    cursor: pointer;
`

function Search(){
    return(
        <SearchContent>
            <SearchForm method="get" action="">
                <SearchInput type="search" placeholder="Busque aqui..."/>
            </SearchForm>

            <SearchImg src={searchImg} alt="search" onBlur={evento => {
                var search = evento.target.value;
                window.location.href = `https://sevenshop.com.br/busca?search=${search}`
            }}/>
        </SearchContent>
    )
}

export default Search
