import styled from "styled-components"
import { useState } from "react"
import searchImg from "../images/search.png"
import { useNavigate } from "react-router-dom";

const SearchContent = styled.div`
    display: flex;
    align-items: center;
    background-color: white;
    height: 2.8rem;
    padding: 0 1rem;
    border-radius: 1rem;
`

const SearchForm = styled.form` 
    display: flex;
    
`

const SearchInput = styled.input`
    width: 30rem;
    background: transparent;
    border: none;
    box-sizing: border-box;

    &:focus-visible {
        outline: none;
    }
`

const SearchButton = styled.button`
    border: none;
    background-color: transparent;
    cursor: pointer;
`

const SearchImg = styled.img`
    width: 1.5rem;
    height: 1.5rem;
    cursor: pointer;
`

const SearchResult = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.5rem;
`

function Search(){
    const [searchInput, setSearchInput] = useState("");
    const navigate = useNavigate(); // Inicializa o hook
  
    const handleSubmit = (e) => {
      e.preventDefault();
      // Lógica de pesquisa ou qualquer outra operação
      console.log(`Pesquisando: ${searchInput}`);
  
      // Redireciona para a página "thank-you" após o envio do formulário
      navigate("/produtos");
    };

    // const [productsFiltered, setproductsFiltered] = useState([]);

    // const [products, setproducts] = useState([]);

    // async function fetchProducts(){
    //     const productsAPI = await getProducts()
    //     setproducts(productsAPI);
    // }

    // useEffect(() => {
    //     fetchProducts();
    // }, [])

    return(
        <SearchContent>
            <SearchForm onSubmit={handleSubmit}>
                <SearchInput type="text" placeholder="Busque aqui..." value={searchInput} onChange={(e) => setSearchInput(e.target.value)}/>
                <SearchButton type="submit">
                    <SearchImg src={searchImg} alt="search"/>
                </SearchButton>
