import styled from "styled-components"
import searchImg from "../images/search.png"
import { useState } from "react";
import { products } from "../data/data.js";

const SearchContent = styled.div`
    display: flex;
    align-items: center;
    background-color: white;
    height: 2.8rem;
    padding: 0 1rem;
    border-radius: 1rem;
`

const SearchForm = styled.form` 
    display: flex;
    
`

const SearchInput = styled.input`
    width: 30rem;
    background: transparent;
    border: none;
    box-sizing: border-box;

    &:focus-visible {
        outline: none;
    }
`

const SearchImg = styled.img`
    width: 1.5rem;
    height: 1.5rem;
    cursor: pointer;
`

function Search(){
    const [productsFiltered,setproductsFiltered] = useState([]);

    return(
        <SearchContent>
            <SearchForm>
                <SearchInput type="text" placeholder="Busque aqui..." onBlur={evento => {

                const search = evento.target.value.trim();
                const productsFill = products.filter(product => product.name.toLowerCase().includes(search.toLowerCase()));
                setproductsFiltered(productsFill);
            }}/>

            </SearchForm>

            <SearchImg src={searchImg} alt="search"/>

            <SearchResult results={productsFiltered}/>   

        </SearchContent>
    )
}

function SearchResult({results}){
    return(
        <div>
            {results.map(product => 
            <p>{product.name}</p>)}
        </div>
    )
}

export default Search
