import styled from "styled-components"
import searchImg from "../images/search.png"
import { products } from "../data/data.js";
import { result, R } from "./result.js";

const SearchContent = styled.div`
    display: flex;
    align-items: center;
    background-color: white;
    height: 2.8rem;
    padding: 0 1rem;
    border-radius: 1rem;
`

const SearchForm = styled.form` 
    display: flex;
    
`

const SearchInput = styled.input`
    width: 30rem;
    background: transparent;
    border: none;
    box-sizing: border-box;

    &:focus-visible {
        outline: none;
    }
`

const SearchImg = styled.img`
    width: 1.5rem;
    height: 1.5rem;
    cursor: pointer;
`

const [productsFiltered,setproductsFiltered] = R();

function Search(){

    return(
        <SearchContent>
            <SearchForm>
                <SearchInput type="text" placeholder="Busque aqui..." onBlur={evento => result(products, evento, setproductsFiltered)}/>

            </SearchForm>

            <SearchImg src={searchImg} alt="search"/>

            productsFiltered

        </SearchContent>
    )
}

export default Search
