import ToggleInput from "./theme-toggle/index.js"
import Link from "../link/index.js"
import Logo from "../logo/index.js"
import styled from "styled-components"

const HeaderContainer = styled.header`
    display: flex;
    height: 4.5rem;   
    align-items: center;
    justify-content: space-around;
    border-top-left-radius: 1.25rem;
    border-top-right-radius: 1.25rem;
    background-color: rgba(70, 2, 119, 0.5);
    margin: 2rem 2rem 0 2rem;
`

const HeaderSearch = styled.form` 
    display: flex;
    height: 2.5rem;
    width: 35rem;
`

const HeaderMenuContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 1rem;
`

const HeaderMenu = styled.ol`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    list-style: none;
    font-size: 1.25rem;
    font-weight: bold;
    gap: 1rem;
    width: 18rem;
`

const HeaderMenuLi = styled.li`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
`

const HeaderTextInput = styled.input`
    height: 100%;
    width: 100%;
    padding: .5rem;
    background-color: white;
    background-image: url(../images/search.png);
    background-size: 1.125rem;
    background-repeat: no-repeat;
    background-position: calc(100% - 0.625rem) center;
    border: none;
    border-radius: 1rem;
    box-sizing: border-box;

    &:focus-visible {
        outline: none;
    }
`

function Header(){
    return(
        <HeaderContainer>
            <HeaderLogo>
                <Logo/>
            </HeaderLogo>
                    
            
                <HeaderSearch method="get" action="">
                    <HeaderTextInput type="search" placeholder="Busque aqui..."/>
                </HeaderSearch>
        
            <HeaderMenuContainer>
                <HeaderMenu>
                    <HeaderMenuLi>
                        <Link href="mailto:suporte@sevenshop.com.br" target="blank">
                            SUPORTE
                        </Link>
                    </HeaderMenuLi>
                    <HeaderMenuLi>
                        <Link href="#contacts">
                            CONTATO
                        </Link>
                    </HeaderMenuLi>

                    <ToggleInput id="theme-toggle"/>
                </HeaderMenu>

            </HeaderMenuContainer>

        </HeaderContainer>
    )
}

export default Header
