import styled from "styled-components"

const HeaderContainer = styled.header`
    display: flex;
    height: 4.5rem;   
    align-items: center;
    justify-content: space-between;
    border-top-left-radius: 1.25rem;
    border-top-right-radius: 1.25rem;
    background-color: rgba(70, 2, 119, 0.5);
    margin: 2rem 2rem 0 2rem;
`

const HeaderContent = styled.div`
            display: flex;
        flex-direction: row;
        align-items: center;
        gap: 15rem;
`

const HeaderTitle = styled.h1`
    font-family: 'Dancing Script', Arial, Helvetica, sans-serif;
    font-size: 2.8rem;
    font-weight: bold;
    margin-left: 5rem;
`

const HeaderLink = styled.a`
    text-decoration: none;
    color: white;
`

const HeaderSearch = styled.form` 
    display: none;
`

const HeaderMenu = styled.ol`
    display: grid;
    grid-template-columns: 1fr 1fr;
    list-style: none;
    font-size: 1.25rem;
    font-weight: bold;
`

const HeaderThemeToogle = styled.input`
    display: none;
`

const HeaderThemeLabel = styled.label`
    display: none;
`

const HeaderTextInput = styled.input`
    height: 100%;
    width: 100%;
    padding-left: 0.625rem;
    background-color: white;
    background-image: url(../images/search.png);
    background-size: 1.125rem;
    background-repeat: no-repeat;
    background-position: calc(100% - 0.625rem) center;
    border: none;
    border-radius: 1rem;
    box-sizing: border-box;

    &:focus-visible {
        outline: none;
    }
`



function Header(){
    return(
        <HeaderContainer>
        <div className="main_header-content">
            <HeaderTitle>
                <HeaderLink href="./index.html">
                Sevenshop
                </HeaderLink>
            </HeaderTitle>
                
            <HeaderSearch method="get" action="">
                <HeaderTextInput type="search" placeholder="Busque aqui..."/>
            </HeaderSearch>
        </div>
        
        <div className="theme">
            <HeaderMenu>
                <li>
                    <HeaderLink href="mailto:suporte@sevenshop.com.br" target="blank">
                    SUPORTE
                    </HeaderLink>
                </li>
                <li>
                    <HeaderLink href="#contacts">
                    CONTATO
                    </HeaderLink>
                </li>
            </HeaderMenu>
            <HeaderThemeToogle type="checkbox"/>
            <HeaderThemeLabel for="theme_toggle"/>
        </div>
    </HeaderContainer>
    )
}

export default Header
