import styled from "styled-components"
import { useState } from "react"
import searchImg from "../images/search.png"

const SearchContent = styled.div`
    display: flex;
    align-items: center;
    background-color: white;
    height: 2.8rem;
    padding: 0 1rem;
    border-radius: 1rem;
`

const SearchForm = styled.form` 
    display: flex;
    
`

const SearchInput = styled.input`
    width: 30rem;
    background: transparent;
    border: none;
    box-sizing: border-box;

    &:focus-visible {
        outline: none;
    }
`

const SearchButton = styled.button`
    border: none;
    background-color: transparent;
    cursor: pointer;
`

const SearchImg = styled.img`
    width: 1.5rem;
    height: 1.5rem;
    cursor: pointer;
`

// const SearchResult = styled.div`
//     display: flex;
//     flex-direction: column;
//     align-items: center;
//     gap: 0.5rem;
// `

function Search({ onSearch }){
    const [productsFiltered,setproductsFiltered] = useState([]);
    const [searchInput, setSearchInput] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        onSearch(searchInput); // Passa o valor da pesquisa para o componente pai
    };

    return(
        <SearchContent>
            <SearchForm onSubmit={handleSubmit}>
                <SearchInput type="text" placeholder="Busque aqui..." value={searchInput} onChange={(e) => setSearchInput(e.target.value)}onBlur={evento => {
            
            var search = evento.target.value;
            const productsFiltered = books.filter(book => book.name.includes(search));
            setproductsFiltered(booksFiltered);
            }}
        />
                <SearchButton type="submit">
                    <SearchImg src={searchImg} alt="search"/>
                </SearchButton>
            </SearchForm>
        </SearchContent>
    )
}

export default Search
