import styled from "styled-components"

const ToggleInputContainer = styled.div`
    display: flex;
    justify-content: center;
    cursor: pointer;
`

const Switch = styled.label`
    position: relative;
    display: inline-block;
    width: 60px;
    height: 34px;

    & #input {
        opacity: 0;
        width: 0;
        height: 0;
    }
`

const Slider = styled.div`
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #2196f3;
    -webkit-transition: 0.4s;
    transition: 0.4s;
    z-index: 0;
    overflow: hidden;

    & .round {
        border-radius: 34px;
    }
  
    &.round .sun-moon {
        border-radius: 50%;
    }
`

const SunMoon = styled.div`
    position: absolute;
    content: "";
    height: 26px;
    width: 26px;
    left: 4px;
    bottom: 4px;
    background-color: yellow;
    -webkit-transition: 0.4s;
    transition: 0.4s;

    & @keyframes cloud-move {
        0% {
            transform: translateX(0px);
        }
    
        40% {
            transform: translateX(4px);
        }
    
        80% {
            transform: translateX(-4px);
        }
    
        100% {
            transform: translateX(0px);
        }
    }
`

const SwitchInput = styled.input`
    &:checked + .slider {
        background-color: black;
    }
  
    &:focus + .slider {
        box-shadow: 0 0 1px #2196f3;
    }
  
    &:checked + .slider .sun-moon {
        -webkit-transform: translateX(26px);
        -ms-transform: translateX(26px);
        transform: translateX(26px);
        background-color: white;
        -webkit-animation: rotate-center 0.6s ease-in-out both;
        animation: rotate-center 0.6s ease-in-out both;
    }

    &:checked + .slider .sun-moon .moon-dot {
        opacity: 1;
    }

    &:checked + .slider .stars {
        -webkit-transform: translateY(0);
        -ms-transform: translateY(0);
        transform: translateY(0);
        opacity: 1;
    }
`

const Svg = styled.Svg`


    & #star-1 {
        width: 20px;
        top: 2px;
        left: 3px;
        animation-delay: 0.3s;
    }
  
    & #star-2 {
        width: 6px;
        top: 16px;
        left: 3px;
    }
    
    & #star-3 {
        width: 12px;
        top: 20px;
        left: 10px;
        animation-delay: 0.6s;
    }
    
    & #star-4 {
        width: 18px;
        top: 0px;
        left: 18px;
        animation-delay: 1.3s;
    }
`

const Stars = styled.div`
    transform: translateY(-32px);
    opacity: 0;
    transition: 0.4s;

    & .star {
        fill: white;
        position: absolute;
        -webkit-transition: 0.4s;
        transition: 0.4s;
        animation-name: star-twinkle;
        animation-duration: 2s;
        animation-iteration-count: infinite;
    }

    & @keyframes star-twinkle {
        0% {
        transform: scale(1);
        }
    
        40% {
        transform: scale(1.2);
        }
    
        80% {
        transform: scale(0.8);
        }
    
        100% {
        transform: scale(1);
        }
    }
`

function ToggleInput(id){
    return(
    <Switch>
        <SwitchInput id="input" type="checkbox" checked="darkTheme" />

        <Slider className="slider round">
            <SunMoon className="sun-moon">
                <Svg id="moon-dot-1" className="moon-dot" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>
                <Svg id="moon-dot-2" className="moon-dot" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>
                <Svg id="moon-dot-3" className="moon-dot" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>
                <Svg id="light-ray-1" className="light-ray" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>
                <Svg id="light-ray-2" className="light-ray" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>
                <Svg id="light-ray-3" className="light-ray" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>

                <Svg id="cloud-1" className="cloud-dark" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>
                <Svg id="cloud-2" className="cloud-dark" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>
                <Svg id="cloud-3" className="cloud-dark" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>
                <Svg id="cloud-4" className="cloud-light" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>
                <Svg id="cloud-5" className="cloud-light" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>
                <Svg id="cloud-6" className="cloud-light" viewBox="0 0 100 100">
                    <circle cx="50" cy="50" r="50"></circle>
                </Svg>

            </SunMoon>

            <Stars>
                <Svg id="star-1" className="star" viewBox="0 0 20 20">
                    <path
                    d="M 0 10 C 10 10,10 10 ,0 10 C 10 10 , 10 10 , 10 20 C 10 10 , 10 10 , 20 10 C 10 10 , 10 10 , 10 0 C 10 10,10 10 ,0 10 Z"/>
                </Svg>
                <Svg id="star-2" className="star" viewBox="0 0 20 20">
                    <path
                    d="M 0 10 C 10 10,10 10 ,0 10 C 10 10 , 10 10 , 10 20 C 10 10 , 10 10 , 20 10 C 10 10 , 10 10 , 10 0 C 10 10,10 10 ,0 10 Z"/>
                </Svg>
                <Svg id="star-3" className="star" viewBox="0 0 20 20">
                    <path
                    d="M 0 10 C 10 10,10 10 ,0 10 C 10 10 , 10 10 , 10 20 C 10 10 , 10 10 , 20 10 C 10 10 , 10 10 , 10 0 C 10 10,10 10 ,0 10 Z"/>
                </Svg>
                <Svg id="star-4" className="star" viewBox="0 0 20 20">
                    <path
                    d="M 0 10 C 10 10,10 10 ,0 10 C 10 10 , 10 10 , 10 20 C 10 10 , 10 10 , 20 10 C 10 10 , 10 10 , 10 0 C 10 10,10 10 ,0 10 Z"/>
                </Svg>
            </Stars>

        </Slider>
    </Switch>

    )
}

export default ToggleInput
