import styled from "styled-components";
import { useEffect, useState } from "react";
import { getProducts } from "../services/products.js";
import { useLocation } from "react-router-dom";

// Estilos do container
const ProductsContainer = styled.div`
  margin: 1rem 6rem 1rem 6rem;
  border: 0.4rem solid white;
  height: 100vh;
  border-radius: 2rem;
  background: linear-gradient(180deg, rgb(46 0 78) 0%, rgb(84 0 133) 100%);
  overflow: hidden;
`;

function Products() {
  const [products, setProducts] = useState([]);
  const [productsFiltered, setproductsFiltered] = useState([]);
  const location = useLocation();

  // Função para pegar parâmetros da URL
  const getQueryParams = () => {
    const urlParams = new URLSearchParams(location.search);
    return urlParams.get('search');
  };

  const searchQuery = getQueryParams();

  // Função assíncrona para buscar os produtos
  async function fetchProducts() {
    const productsAPI = await getProducts();
    setProducts(productsAPI);
  }

  useEffect(() => {
    fetchProducts();
  }, []);

  // UseEffect para filtrar os produtos com base na pesquisa
  useEffect(() => {
    const productsFill = products.filter(product =>
      product.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setproductsFiltered(productsFill);
  }, [searchQuery, products]);

  // Usando require.context para importar as imagens dinamicamente
  const images = require.context('../assets/products', false, /\.(png|jpe?g|gif)$/);

  return (
    <ProductsContainer>
      {productsFiltered.map((product) => {
        // Caminho da imagem com base no atributo src do produto
        const imagePath = `./${product.src}.png`;  // Adapte a extensão conforme necessário
        try {
          const image = images(imagePath);
          return (
            <div key={product.id}>
              <p>{product.name}</p>
              <img src={image} alt={product.name} />
            </div>
          );
        } catch (error) {
          console.error(`Erro ao carregar a imagem para o produto ${product.name}:`, error);
          return (
            <div key={product.id}>
              <p>{product.name}</p>
              <p>Imagem não disponível</p>
            </div>
          );
        }
      })}
    </ProductsContainer>
  );
}

export default Products;
