import { useEffect, useState } from "react";
import styled from "styled-components";
import banner from "../assets/images/Banner.png"
import ProductsStyled from "../assets/products/index.js";

const HomeContainer = styled.div`
    display: flex;
    justify-content: center;
    margin: 0 2rem;
    border: 0.4rem solid white;
    border-top: none;
    height: 100vh;
    border-radius: 0 0 2rem 2rem;
    background: linear-gradient(180deg, rgb(46 0 78) 0%, rgb(84 0 133) 100%);
    overflow: hidden;
`

const Banner = styled.img`
    width: 100%;
    height: 35rem;
`

function Home() {
    const [ products, setProducts ] = useState([]);
    async function fetchProducts() {
        const productsAPI = await getProducts();
        setProducts(productsAPI);
    }
    useEffect(() => {
        fetchProducts();
    }, []);

    return (

      <HomeContainer>
        <Banner src={banner}/>

        {products.map(product => {
            return (
              <ProductsStyled name={product.name} image={image} price={product.price} newprice={product.newprice} src={product.src}/>
            )
        })}

      </HomeContainer>

    );  
}

export default Home;
