import styled from "styled-components";
import { useEffect, useState } from "react";
import { getProducts } from "../services/products.js";
import { useLocation } from "react-router-dom";

const ProductsContainer = styled.div`
  margin: 1rem 6rem 1rem 6rem;
  border: 0.4rem solid white;
  height: 100vh;
  border-radius: 2rem;
  background: linear-gradient(180deg, rgb(46 0 78) 0%, rgb(84 0 133) 100%);
  overflow: hidden;
`

function Products() {
    const location = useLocation();

    const getQueryParams = () => {
      const urlParams = new URLSearchParams(location.search);
      return urlParams.get('search');
    };
  
    const searchQuery = getQueryParams();

    const [ products, setProducts ] = useState([]);

    async function fetchProducts() {
        const productsAPI = await getProducts();
        setProducts(productsAPI);
    }

    useEffect(() => {
        fetchProducts();
    }, []);

    const [productsFiltered, setproductsFiltered] = useState([]);

    const productsFill = products.filter(product => product.name.includes(searchQuery));
    setproductsFiltered(productsFill);

    return (

        <ProductsContainer>

        {productsFiltered.map(book => {
                    return (
                        <SearchResult onClick={() => insertFavorite(book.id)}>
                            <p>{book.name}</p>
                        </SearchResult>
                    )
                })}

        </ProductsContainer>

    );  
}

export default Products;
