import ThemeToggle from "./theme-toggle/index.js"
import styled from "styled-components"

const HeaderContainer = styled.header`
    display: flex;
    height: 4.5rem;   
    align-items: center;
    justify-content: space-between;
    border-top-left-radius: 1.25rem;
    border-top-right-radius: 1.25rem;
    background-color: rgba(70, 2, 119, 0.5);
    margin: 2rem 2rem 0 2rem;
`

const HeaderContent = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    gap: 15rem;
`

const HeaderTitle = styled.h1`
    font-family: 'Dancing Script', Arial, Helvetica, sans-serif;
    float: left;
    font-size: 2.8rem;
    font-weight: bold;
    margin-left: 5rem;
`

const HeaderLink = styled.a`
    text-decoration: none;
    color: white;
`

const HeaderSearch = styled.form` 
    display: grid;
    height: 2.5rem;
    width: 35rem;
`

const HeaderMenuContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 1rem;
`

const HeaderMenu = styled.ol`
    display: grid;
    align-items: center;
    justify-content: center;
    grid-template-columns: 1fr 1fr;
    list-style: none;
    font-size: 1.25rem;
    font-weight: bold;
`

const HeaderMenuLi = styled.li`
    display: flex;
    justify-content: center;
    align-items: center;
    width: 7rem;
    flex-direction: column;
`

const HeaderTextInput = styled.input`
    height: 100%;
    width: 100%;
    padding: .5rem;
    background-color: white;
    background-image: url(../images/search.png);
    background-size: 1.125rem;
    background-repeat: no-repeat;
    background-position: calc(100% - 0.625rem) center;
    border: none;
    border-radius: 1rem;
    box-sizing: border-box;

    &:focus-visible {
        outline: none;
    }
`



function Header(){
    return(
        <HeaderContainer>
        <HeaderContent>
            <HeaderTitle>
                <HeaderLink href="./index.html">
                Sevenshop
                </HeaderLink>
            </HeaderTitle>
                
            <HeaderSearch method="get" action="">
                <HeaderTextInput type="search" placeholder="Busque aqui..."/>
            </HeaderSearch>
        </HeaderContent>
        
        <HeaderMenuContainer>
            <HeaderMenu>
                <HeaderMenuLi>
                    <HeaderLink href="mailto:suporte@sevenshop.com.br" target="blank">
                    SUPORTE
                    </HeaderLink>
                </HeaderMenuLi>
                <HeaderMenuLi>
                    <HeaderLink href="#contacts">
                    CONTATO
                    </HeaderLink>
                </HeaderMenuLi>
            </HeaderMenu>

            <ThemeToggle/>

        </HeaderMenuContainer>

        </HeaderContainer>
    )
}

export default Header
