import styled from "styled-components"
import searchImg from "../images/search.png"
import { useState } from "react";
import { products } from "../data/data.js";

const SearchContent = styled.div`
    display: flex;
    align-items: center;
    background-color: white;
    height: 2.8rem;
    padding: 0 1rem;
    border-radius: 1rem;
`

const SearchForm = styled.form` 
    display: flex;
    
`

const SearchInput = styled.input`
    width: 30rem;
    background: transparent;
    border: none;
    box-sizing: border-box;

    &:focus-visible {
        outline: none;
    }
`

const SearchImg = styled.img`
    width: 1.5rem;
    height: 1.5rem;
    cursor: pointer;
`

const SearchResult = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.5rem;
`

function Result(){
    const productsFiltered = products.filter(product => product.name.toLowerCase().includes(search.toLowerCase()));
}

function Search(){
    const [productsFiltered,setproductsFiltered] = useState([]);

    return(
        <SearchContent>
            <SearchForm>
                <SearchInput type="text" placeholder="Busque aqui..." onBlur={evento => {
                    const search = evento.target.value.trim();
                    
                    {}

                    setproductsFiltered(productsFiltered);
                }}/>
            </SearchForm>

            <SearchImg src={searchImg} alt="search"/>

            {productsFiltered.map((product) => (
                <SearchResult>
                    <p>{product.name}</p>
                    <img src={product.src} alt={product.name} />
                </SearchResult>
            ))}
            
        </SearchContent>
    )
}

export default Search
