import styled from "styled-components"
import { useEffect, useState } from "react"
import searchImg from "../images/search.png"
import { getProducts } from "../../services/products.js"

const SearchContent = styled.div`
    display: flex;
    align-items: center;
    background-color: white;
    height: 2.8rem;
    padding: 0 1rem;
    border-radius: 1rem;
`

const SearchForm = styled.form` 
    display: flex;
    
`

const SearchInput = styled.input`
    width: 30rem;
    background: transparent;
    border: none;
    box-sizing: border-box;

    &:focus-visible {
        outline: none;
    }
`

const SearchButton = styled.button`
    border: none;
    background-color: transparent;
    cursor: pointer;
`

const SearchImg = styled.img`
    width: 1.5rem;
    height: 1.5rem;
    cursor: pointer;
`

// const SearchResult = styled.div`
//     display: flex;
//     flex-direction: column;
//     align-items: center;
//     gap: 0.5rem;
// `

function Search({ onSearch }){
    const [productsFiltered, setproductsFiltered] = useState([]);
    const [searchInput, setSearchInput] = useState("");

    const [products, setproducts] = useState([]);

    async function fetchProducts(){
        const productsAPI = await getProducts()
        setproducts(productsAPI);
    }

    useEffect(() => {
        fetchProducts();
    }, [])

    const handleSubmit = (e) => {
        e.preventDefault();
        onSearch(searchInput);
    };

    return(
        <SearchContent>
            <SearchForm onSubmit={handleSubmit}>
                <SearchInput type="text" placeholder="Busque aqui..." onBlur={evento => {
            
            var search = evento.target.value;
            const productsFill = products.filter(products => products.name.includes(search));
            setproductsFiltered(productsFill);
        }}/>
                <SearchButton type="submit">
                    <SearchImg src={searchImg} alt="search"/>
                </SearchButton>
            </SearchForm>

            {productsFiltered.map(product => {
                    return (
                        <div>
                            <p>{product.name}</p>
                        </div>
                    )
                })}
        </SearchContent>
    )
}

export default Search
