import ToggleInput from "./theme-toggle/index.js"
import Link from "../link/index.js"
import Logo from "../logo/index.js"
import styled from "styled-components"

const HeaderContainer = styled.header`
    display: flex;
    height: 4.5rem;   
    align-items: center;
    justify-content: space-around;
    border-top-left-radius: 1.25rem;
    border-top-right-radius: 1.25rem;
    background-color: rgba(70, 2, 119, 0.5);
    margin: 2rem 2rem 0 2rem;
`

const HeaderLogoContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    width: 20rem;
`

const HeaderSearchContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
`

const HeaderSearch = styled.form` 
    display: flex;
    align-items: center;
    justify-content: center;
    height: 2.5rem;
    width: 35rem;
`

const HeaderSearchInput = styled.input`
    height: 2.8rem;
    width: 35rem;
    text-align: center;
    padding: .5rem;
    background-color: white;
    background-image: url(../images/search.png);
    background-size: 1.125rem;
    background-repeat: no-repeat;
    background-position: calc(100% - 0.625rem) center;
    border: none;
    border-radius: 1rem;
    box-sizing: border-box;

    &:focus-visible {
        outline: none;
    }
`

const HeaderMenuContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
`

const HeaderMenu = styled.ol`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    list-style: none;
    font-size: 1.25rem;
    font-weight: bold;
    gap: 2rem;
    width: 20rem;
`

const HeaderMenuLi = styled.li`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
`

function Header(){
    return(
        <HeaderContainer>
            <HeaderLogoContainer>
                <Logo/>
            </HeaderLogoContainer>
                    
            <HeaderSearchContainer>
                <HeaderSearch method="get" action="">
                    <HeaderSearchInput type="search" placeholder="Busque aqui..."/>
                </HeaderSearch>
            </HeaderSearchContainer>
        
            <HeaderMenuContainer>
                <HeaderMenu>
                    <HeaderMenuLi>
                        <Link href="mailto:suporte@sevenshop.com.br" target="blank">
                            SUPORTE
                        </Link>
                    </HeaderMenuLi>
                    <HeaderMenuLi>
                        <Link href="#contacts">
                            CONTATO
                        </Link>
                    </HeaderMenuLi>

                    <ToggleInput id="theme-toggle"/>
                </HeaderMenu>

            </HeaderMenuContainer>

        </HeaderContainer>
    )
}

export default Header
