import styled from "styled-components";
import { useEffect, useState } from "react";
import { getProducts } from "../services/products.js";
import { useLocation, Link } from "react-router-dom";

const ProductsContainer = styled.div`
  margin: 1rem 6rem 1rem 6rem;
  border: 0.4rem solid white;
  height: 100vh;
  border-radius: 2rem;
  background: linear-gradient(180deg, rgb(46 0 78) 0%, rgb(84 0 133) 100%);
  overflow: hidden;
`


function Products() {
    const [ products, setProducts ] = useState([]);
    const [productsFiltered, setproductsFiltered] = useState([]);
    const location = useLocation();

    const getQueryParams = () => {
      const urlParams = new URLSearchParams(location.search);
      return urlParams.get('search');
    };
  
    const searchQuery = getQueryParams();

    async function fetchProducts() {
        const productsAPI = await getProducts();
        setProducts(productsAPI);
    }

    useEffect(() => {
        fetchProducts();
    }, []);

    useEffect(() => {
        const productsFill = products.filter(product => product.name.toLowerCase().includes(searchQuery.toLowerCase()));
        setproductsFiltered(productsFill);
    }, [searchQuery, products]);

    const images = require.context('../assets/products', false, /\.(png|jpe?g|gif)$/)
    
    return (

        <ProductsContainer>

        {productsFiltered.map(product => {
            const imagePath = `./${product.src}.png`;
    
            const image = images(imagePath);

            return (
                <ol>
                    <li>
                        <img src={image} alt={product.name}/>
                        <div class="description">
                            <h4>
                                {product.name}
                            </h4>
                            <h5>
                                {product.price}
                            </h5>
                            <h3>
                                {product.newprice}
                            </h3>
                            <h6 class="pay">√Å vista</h6>

                            <div class="buy-button">

                                <button class="buy">
                                    <Link>
                                        COMPRAR
                                    </Link>
                                </button>
                            </div>

                        </div>
                    </li>


                    
                    
                    
                </ol>
            )
        })}
        </ProductsContainer>

    );  
}

export default Products;
