import styled from "styled-components"

const ToggleInputContainer = styled.div`
    display: flex;
    justify-content: center;
    width: 7rem;
    cursor: pointer;
`

const ThemeInput = styled.input`
    position: absolute;
    left: -9999px;

    &:checked + .theme-label::before{
        background-color: rgb(0, 132, 255);
    }
        
    &:checked + .theme-label::after{
        left: 1.6rem;
    }
`

const ThemeLabel = styled.label`
    display: flex;
    position: relative;
    cursor: pointer;

    &::before, &::after{
        content: "";
        transition: all 0.2s ease;
    }

    &::before{
        display: flex;
        width: 2.8rem;
        height: 1.4rem;
        background-color: rgb(94, 0, 170);
        border-radius: 1rem;
    }

    &::after{
        position: absolute;
        top: 0.2rem;
        left: 0.2rem;
        width: 1rem;
        height: 1rem;
        background-color: #fff;
        border-radius: 100%;
    }
`

function ToggleInput(id){
    return(
        <ToggleInputContainer>
            <ThemeInput type="checkbox" id={id}/>
            <ThemeLabel className="theme-label" for={id}/>
        </ToggleInputContainer>
    )
}

export default ToggleInput
